import { useState } from "react"
import { useDispatch, useSelector } from "react-redux"
import { decrement, increment,incrementByAmount,decrementByAmount } from "./counterSlice"
import './style.css'


export default function Counter(){
    const count = useSelector((state)=>state.counter.value)
    const dispatch = useDispatch()
    const [value,setValue] = useState()
    const handleChange = (e) => {
     if(e==='increment'){
        dispatch(incrementByAmount(value))
    }
     else {
        dispatch(decrementByAmount(value))
     }
    }
    return (
        <div>
            <div>
                <header className="App-header">
                <img src='data:image/svg+xml;base64,' alt='logo' className="App-logo" />
                </header>
                <button className="btn"
                onClick={()=>dispatch(increment())}>
                    +
                </button>
                <span id='count'>{count}</span>
                <button className="btn"
                onClick={()=>dispatch(decrement())}>
                    -
                </button>
            </div>
            <div className="lower">
            <input className="input" type='number' value={value} onChange={e=>setValue(e.target.value)} />
            <button className="btn" onClick={()=>dispatch(incrementByAmount(value))}>Add Amount</button>
            <select className="btn" onChange={(e)=>handleChange(e.target.value)}>
                <option value='increment'>Increment</option>
                <option value='decrement'>Decremenet</option>
            </select>
            </div>
        </div>
    )
}